if($('#nav-in').length) {
    
    $("#nav-top").hide(); //Hide the navigation bar first

    $(window).scroll(function () {  //Listen for the window's scroll event
        if (isScrolledAfterElement("#title")) { //if it has scrolled beyond the #content elment
            $('#nav-top').show();  //Show the navigation bar
        } else {
            $('#nav-top').hide(); //Else hide it
        }
    });

    //Function that returns true if the window has scrolled beyond the given element
    function isScrolledAfterElement(elem) {
        var $elem = $(elem);
        if($elem.length) {
            var $window = $(window);

            var docViewTop = $window.scrollTop();

            var elemTop = $elem.offset().top;
            return elemTop <= docViewTop;
        }
        else
            return true;
    }
}
